import React, { useState } from "react";
import axios from "axios";

export default function AddFertilizer() {
  const [fertilizerName, setName] = useState("");
  const [fertilizerType, setType] = useState("");
  const [manufacturer, setManufacturer] = useState("");
  const [quantity, setQuantity] = useState("");
  const [error, setError] = useState(""); // State to store backend validation errors

  function sendData(e) {
    e.preventDefault();

    const newFertilizer = {
      fertilizerName,
      fertilizerType,
      manufacturer,
      quantity
    };

    axios.post("http://localhost:8070/fertilizer/add", newFertilizer)
      .then(() => {
        alert("Fertilizer Added");
        setName("");
        setType("");
        setManufacturer("");
        setQuantity("");
        setError(""); // Reset error state after successful submission
      })
      .catch((err) => {
        if (err.response && err.response.data) {
          setError(err.response.data.message); // Set error message from server response
        } else {
          setError("An error occurred. Please try again."); // Generic error message
        }
      });
  }

  return (
    <div style={{ display: "flex", justifyContent: "center" }}>
      <form style={{ width: "500px" }} onSubmit={sendData}>
        {/* Display error message if present */}
        {error && <div className="alert alert-danger">{error}</div>}

        <div className="form-group">
          <label htmlFor="fertilizerName">Fertilizer Name</label>
          <input
            type="text"
            className="form-control"
            id="fertilizerName"
            placeholder="Enter fertilizer name"
            value={fertilizerName}
            onChange={(e) => setName(e.target.value)}
          />
        </div>

        {/* Repeat similar structure for other form fields */}

        <button type="submit" className="btn btn-primary">
          Submit
        </button>
      </form>
    </div>
  );
}
